function [kernel] = kernel(vec1, vec2, method, param1, param2)  if nargin == 2    method = 'linear';  endif    switch(method)    case 'linear'      kernel = dot(vec1, vec2);    case 'poly'      kernel = (dot(vec1, vec2) + param2).^param1;    case 'rbf'      kernel = e.^(-1 / (2 * param1) .* (norm(vec1 - vec2).^2));    case 'sgm'        kernel = tanh(param1 * dot(vec1, vec2) + param2);  endswitch  
endfunction
