function [decision] = kernelized_decision(observation,                                          predictors,                                          classes,                                          multipliers,                                          kernel_mode,                                           param1,                                          param2)                                                       if nargin == 6    param2 = 'whatever';  endif    if nargin == 4 || (nargin == 5 && kernel_mode == 'linear')    param2 = 'whatever';    param1 = 'whatever';    kernel_mode = 'linear';  endif    kernelized_prediction = 0;  for j=1:length(classes)    kernelized_prediction = kernelized_prediction + multipliers(j) * classes(j) * kernel(observation, predictors(j, :), kernel_mode, param1, param2);  endfor    b = kernelized_bias(multipliers, predictors, classes, kernel_mode, param1, param2);  decision = sign(kernelized_prediction + b);
endfunction
